    def loadData(self):
        y = database.jobCollection.find({})
        for w in y:
            print(w)
        self.table_sydney.clearContents()
        if self.comboBox_6.currentText() == "All":
            x = database.jobCollection.find({})
        elif self.comboBox_6.currentText() == "Sydney":
            x = database.jobCollection.find({"location": "Sydney"})
        elif self.comboBox_6.currentText() == "Brisbane":
            x = database.jobCollection.find({"location": "Brisbane"})
        elif self.comboBox_6.currentText() == "Boondall":
            x = database.jobCollection.find({"location": "Boondall"})
        row = 0
        for result in x:
            self.table_sydney.insertRow(row)
            col = 0
            for elem in result:
                # print(result[elem])
                self.table_sydney.setItem(row, col, QtWidgets.QTableWidgetItem(str(result[elem])))
                col += 1
            row += 1

    def printInput(self):
        c = database.CustomerDoc(name=self.text_c_1.toPlainText(),
                                 company=self.text_c_2.toPlainText(),
                                 phone=self.text_c_3.toPlainText(),
                                 address=self.text_c_4.toPlainText(),
                                 email=self.text_c_5.toPlainText(),
                                 partner=self.text_c_6.currentText())

        d = database.DeviceDoc(type=self.text_d_1.toPlainText(),
                               media=self.text_d_2.toPlainText(),
                               serial=self.text_d_3.toPlainText(),
                               recdate=self.text_d_4.toPlainText(),
                               extras=self.text_d_5.currentText(),
                               patient=self.text_d_6.toPlainText()
                               )
        j = database.JobDoc(customername=c.name,
                            devicemedia=d.media,
                            deviceserial=d.serial,
                            quote=self.text_j_1.toPlainText(),
                            priority=self.text_j_4.currentText(),
                            recdate=d.recdate,
                            startdate=self.text_j_2.toPlainText(),
                            enddate=self.text_j_3.toPlainText(),
                            location=self.text_j_5.currentText(),
                            notes = self.text_j_7.toPlainText(),
                            success=self.text_j_6.currentText())

        c.addToMongo()
        d.addToMongo()
        j.addToMongo()

    def deleteClicked(self):
        button = self.sender()
        if button:
            row = self.table.indexAt(button.pos()).row()
            self.table.removeRow(row)

    def clearData(self):
        results = database.jobCollection.find({})
        for i in results:
            print(i)

        database.jobCollection.delete_many({})
        database.deviceCollection.delete_many({})
        database.contactCollection.delete_many({})

        results = database.jobCollection.find({})
        for i in results:
            print(i)

        self.loadData()

    def updateRows(self):
        updated = self.table_sydney.selectedItems()
        for x in updated:
            print(x[0])

